import{_ as e,c as t,o,a as n}from"./app.8d8628cf.js";var r="/notes/assets/tcp-segment-header.609bd68b.svg";const T=JSON.parse('{"title":"TCP \u63E1\u624B","description":"","frontmatter":{},"headers":[],"relativePath":"common/tcp.md"}'),s={name:"common/tcp.md"},a=n('<h1 id="tcp-\u63E1\u624B" tabindex="-1">TCP \u63E1\u624B <a class="header-anchor" href="#tcp-\u63E1\u624B" aria-hidden="true">#</a></h1><blockquote><p>TCP handshake</p></blockquote><p>TCP \u4F20\u8F93\u63A7\u5236\u534F\u8BAE(Transmission Control Protocol) \u4F7F\u7528<strong>\u4E09\u6B21\u63E1\u624B</strong>(<strong>three-way handshake</strong>)\u5728\u57FA\u4E8E IP \u7684\u7F51\u7EDC\u4E0A\u5EFA\u7ACB TCP/IP \u8FDE\u63A5</p><p>TCP \u53D1\u9001\u7684\u7528\u4E8E\u534F\u5546\u548C\u542F\u52A8 TCP \u4F1A\u8BDD\u7684\u4E09\u4E2A\u6D88\u606F\u5206\u522B\u79F0\u4E3A SYN, SYN-ACK, \u548C ACK\uFF0C \u5206\u522B\u4EE3\u8868 <strong>SYN</strong>chronize, <strong>SYN</strong>chronize-<strong>ACK</strong>nowledgement \u548C <strong>ACK</strong>nowledge. \u4E09\u4E2A\u6D88\u606F\u673A\u5236\u7684\u8BBE\u8BA1\u662F\u4E3A\u4E86\u8BA9\u4E24\u53F0\u60F3\u8981\u6765\u56DE\u4F20\u9012\u4FE1\u606F\u7684\u8BA1\u7B97\u673A\u53EF\u4EE5\u5728\u4F20\u8F93 HTTP \u6D4F\u89C8\u5668\u8BF7\u6C42\u7B49\u6570\u636E\u4E4B\u524D\u534F\u5546\u8FDE\u63A5\u7684\u53C2\u6570</p><p>\u4E3B\u673A\uFF0C\u901A\u5E38\u662F\u6D4F\u89C8\u5668\uFF0C\u5411\u670D\u52A1\u5668\u53D1\u9001\u4E00\u4E2A TCP SYNchronize \u6570\u636E\u5305\u3002 \u670D\u52A1\u5668\u63A5\u6536\u5230 SYN \u5E76\u8FD4\u56DE\u4E00\u4E2A SYNchronize-ACKnowledgement. \u4E3B\u673A\u63A5\u6536\u5230\u670D\u52A1\u5668\u7684 SYN-ACK \u5E76\u53D1\u9001\u4E00\u4E2A ACKnowledge. \u670D\u52A1\u5668\u6536\u5230 ACK \u5E76\u5EFA\u7ACB TCP \u5957\u63A5\u5B57\u8FDE\u63A5</p><p>\u5728\u521B\u5EFA\u5B89\u5168\u8FDE\u63A5\u65F6\uFF0C\u6B64\u63E1\u624B\u6B65\u9AA4\u53D1\u751F\u5728 DNS \u67E5\u627E(DNS lookup)\u4E4B\u540E\u548C TLS \u63E1\u624B(TLS handshake)\u4E4B\u524D\u3002 \u8FDE\u63A5\u7684\u6BCF\u4E00\u4FA7\u90FD\u53EF\u4EE5\u901A\u8FC7\u56DB\u6B21\u63E1\u624B\u72EC\u7ACB\u7EC8\u6B62\u8FDE\u63A5</p><p><img src="'+r+'" alt="url-all"></p>',7),c=[a];function p(_,d,i,h,l,g){return o(),t("div",null,c)}var m=e(s,[["render",p]]);export{T as __pageData,m as default};
